import React from "react";

const Header = ({ title, numMovies }) => {
  return (
    <div className="row">
      <div className="justify-content-md-center">
        <h2>
          {`${title}  `}
          <span className="badge badge-pill badge-success">{numMovies}</span>
        </h2>
      </div>
    </div>
  );
};

export default Header;
