import React, { useState, createContext } from "react";
import { login } from "../api/movie-api";

export const AuthContext = createContext(null);

const AuthContextProvider = (props) => {
  const existingToken = localStorage.getItem("token");
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [authToken, setAuthToken] = useState(existingToken);
  const [userName, setUserName] = useState("");

  //Function to put JWT token in local storage.
  const setToken = (data) => {
    localStorage.setItem("token", data);
    setAuthToken(data);
  }

  const authenticate = async (username, password) => {
    const result = await login(username, password);
    if (result.token) {
      setToken(result.token)
      setIsAuthenticated(true);
      setUserName(username);
    }
  };

//   const register = async (username, password) => {
//     const result = await signup(username, password);
//     console.log(result.code);
//     return (result.code === 201) ? true : false;
//   };

//   const signout = () => {
//     setTimeout(() => setIsAuthenticated(false), 100);
//   };

  //get favourites for the logged in user
//   const getUserFavourites = async (username) => {
//     const movies = await getFavourites(username);
//     return (movies);
//   };

//   const getUserWatchLater = async (username) => {
//     const movies = await getWatchList(username);
//     return (movies);
//   };

  return (
    <AuthContext.Provider
      value={{
        isAuthenticated,
        authenticate,
        // register,
        // signout,
        userName,
        // getUserFavourites,
        // getUserWatchLater,
      }}
    >
      {props.children}
    </AuthContext.Provider>
  );
};

export default AuthContextProvider;
