import React, { useContext } from "react";
import "./filterControls.css";
import { GenresContext } from '../../contexts/genresContext' 

const FilterControls = props => {
  const context = useContext(GenresContext);

  const handleChange = (e, type, value) => {
    e.preventDefault();
    props.onUserInput(type, value);
  };
  const handleTextChange = e => {
    handleChange(e, "name", e.target.value);
  };
  const handleGenreChange = e => {
    handleChange(e, "genre", e.target.value);
  };

  return (


    
    <div className="newsearch ">
      <div className=" ">
        <h4>
          <span className="newstyle">List Filtering:</span>
          <input
            type="text"
            placeholder="Title Search"
            onChange={handleTextChange} className="newstyle"
          />
          <span className="newstyle">Genre:</span>
          <select id="genre" onChange={handleGenreChange} className="newstyle">
            {context.genres.map(genre => {
              return (
                <option key={genre.id} value={genre.id}>
                  {genre.name}
                </option>
              );
            })}
          </select>
        </h4>
      </div>
      
    </div>



  );
  const SiteHeader = () => {
    return (
      
      <nav className="navbar fixed-top newhead ">
        
        <nav className="navbar-brand text-white">
          <Link className=" text-white" to="/">
            TMDB Client
          </Link>
        </nav>
        <FontAwesomeIcon
          className="navbar-text text-light"
          icon={["fas", "video"]}
          size="3x"
        />
        <span className="navbar-text text-light">
          For the movie enthusiast !!
        </span>
        <FontAwesomeIcon
          className="navbar-text text-light"
          icon={["fas", "film"]}
          size="3x"
        />
        <nav className="navbar navbar-expand ">
          <ul className="navbar-nav">
            <li className="nav-item">
              <Link className="nav-link text-white" to="/">
                Home
              </Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link text-white" to="/movies/favorites">
                Favorites
              </Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link text-white" to="/movies/upcoming">
                Upcoming Movies
              </Link>
            </li>
          </ul>
        </nav>
        
      </nav>
    );
  };
};



export default FilterControls;
